import './index.css'
import React from 'react'
import { Link } from 'react-router-dom'


const Planet = ({ data }) => {
    const size = Math.round(data.equaRadius / 250)
    const styles = {
        width: size || 1,
        height: size || 1,
        borderRadius: size / 2 || 1,
        margin: '10px auto'
    }
    return (
        <Link to={`/planet/${data.id}`} title={`Details data for ${data.englishName}`}>
            <div className="col-sm-4 text-center planet-card thumbnail">
                <h4>
                    {data.englishName}
                </h4>
                <div className="jcenter">
                    <div className="planet-round" style={styles} ></div>
                </div>
            </div>
        </Link>
    )
}

export default Planet