import React, { useEffect, useState } from 'react'
import axios from 'axios'

import Planet from 'components/planet'
import Search from 'components/search'
import Loader from 'components/loader'


const ListPlanets = () => {
    const [planets, setPlanets] = useState([])
    const [isLoading, setIsLoading] = useState(false)

    const fetchPlanets = () => {
        setIsLoading(true)
        const apiUrl = 'https://api.le-systeme-solaire.net/rest/bodies/?order=perihelion,desc&filter[]=isPlanet,neq,false'
        const req = axios.get(apiUrl)
        req.then(res => {
            setPlanets(res.data.bodies)
            setIsLoading(false)
        }).catch(err => {
            console.log(err.errors)
        })
    }

    useEffect(() => {

        if (planets.length > 0) {
            return
        }
        fetchPlanets()
    }, [planets])

    if (isLoading) {
        return <Loader />
    }

    return (
        <div className="container">
            <div className="row">
                <div className="col-sm-3 pull-right">
                    <Search />
                </div>
            </div>
            <h1 className="text-center">Solar system planets</h1>
            <div className="row">
                {planets.map(item => (
                    <Planet data={item} key={item.id} />
                ))}
            </div>
        </div>
    )
}

export default ListPlanets